import React, { useEffect, useState } from "react";
import { list } from "./Api";
import { Hire } from "./NewResources";

export const Home=()=>{

    const[localArr,setlocalArr]=useState([])
    const[createView,setCreateView]=useState(false)

    
const hai=()=>
{
    setlocalArr(list())
}

// useEffect(()=>
// {
//     hai()
// },[]

//)
    
    return(

        <>
        <div>
            
        if(createView===true)
        {
            <>
        <Recruite/>
                    <button className="btn btn-outline-secondary" onClick={
                        ()=>{
                            setCreateView(false)
                        }
                    }>
                        Back
                    </button>
             
        </>
}
        else{
                <>
                <button className="btn btn-outline-success mb-3"
                onClick={()=>{
                    setCreateView(true)
                }}>
                Recruite New Employee
                </button>
        
         </>
            }

        </div>
        
           
        <Hire/>   
        <div className="container">

         <div className="row justify-content-center">
                        <div className="table-responsive-md">
                            <table className="table table-striped" >
                                <thead>
                                    <tr>
                                        <th> Name</th>
                                        <th> Id</th>
                                        
                                    </tr>
                                </thead>
                                <tbody>
                                    {localArr.map((elements)=>(
                                        <tr>
                                            <td>{elements.proName}</td>
                                            <td>{elements.proId}</td>
                                            
                                        </tr>
                                    ))}
                                    <button onClick={hai}>View</button>
                                </tbody>
                            </table>
                        </div>
                    </div> 
                    </div>
        
        </>


    )


}
