import React, { useEffect, useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css"
import "../node_modules/bootstrap-icons/font/bootstrap-icons.css"
import { list } from "./Api";
import { Hire } from "./NewResources";
import { Reading } from "./Reading";
import { Update } from "./Update";
import { gather } from "./Axiosurl";
// import { read } from "./Api";



export const Home=()=>{

    const[localArr,setlocalArr]=useState([])
    const[createView,setCreateView]=useState(false)
    const[readView,setreadView]=useState(false)
    const[updateView,setupdateView]=useState(false)
    const[pos,setPos]=useState(0)
    const[whole,setWhole]=useState({})


    
const hai=async()=>
{
    const tee=await gather();
    setlocalArr(tee.data)
}

useEffect(()=>
{
    hai()
},[]

)

const toErase=async()
  return(

  <div>          
{(createView)?
<>
<Hire/>
<button className="btn btn-outline-warning"
onClick={

    ()=>
    {
        setCreateView(false)
        window.location.assign("/")
    }}>
</button>
back
</>
:
(updateView)?
<>
<Update mention={whole}/>
<button className="btn btn-outline-warning" 
onClick={
()=>{

    setupdateView(false)
    window.location.assign("/")

}
}>
    <i className="bi bi-skip-backward-btn-fill"></i> Back

</button>
</>
:
(readView)?
<>
<Reading who={pos}/>
    <button className="btn btn-outline-secondary" onClick={
        ()=>{
            setreadView(false)
        }
    }>
        Back
    </button>
</>
:
<>
<button className="btn btn-outline-success mb-3"
    onClick={()=>{
        setCreateView(true)
    }}>
AddNew
</button>
          
        <div className="container">

            <div className="row justify-content-center">
                        <div className="table-responsive-md">
                            <table className="table table-striped" >
                                <thead>
                                    <tr>
                                        <th> Name</th>
                                        <th> Rollno</th>
                                        <th>Location</th>
                                        <th>Skills</th>
                                        <th>Action</th>
                                        
                                    </tr>
                                </thead>
                                <tbody>
                                    {localArr.map((elements,index)=>(
                                        <tr>
                                            <td>
                                                <button class="btn btn-outline-primary" onClick={()=>{
                                                    setreadView(true)
                                                    setPos(elements.proId) //elements.proId ?
                                                }}>
                                                    <i class="bi bi-book-half"></i>
                                                </button>
                                                {elements.proName}
                                            </td>
                                            <td>{elements.proRollno}</td>
                                            <td>{elements.proLoc}</td>
                                            <td>{elements.proSkills}</td>
                                            <td>
                                                <button className="btn btn-outline-warning" 
                                                onClick={()=>{

                                                        setupdateView(true)

                                                       // setPos(index)
                                                        //const u=fetch(elements.proName)
                                                        setWhole(elements)

                                                }}>
                                                    Edit <i class="bi bi-pencil-fill"></i>
                                                </button>
                                            </td>
                                            <td>
                                                <button className="btn btn-outline-danger">
                                                    Delete <i class="bi bi-trash-fill"></i>
                                                </button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                    </div>
                    </div> 
                    </div>
        
                    </>  
        }
        </div>
                               
    )
  

}
