import React, { useEffect, useState } from "react";
import { list } from "./Api";
import { Hire } from "./NewResources";

export const Home=()=>{

    const[localArr,setlocalArr]=useState([])
    const[createView,setCreateView]=useState(false)

    
const hai=()=>
{
    setlocalArr(list())
}

useEffect(()=>
{
    hai()
},[]

)

return(
    <>
        
        <div className="container mt-5">
           
            {(createView)?
            <>
                <Recruite/>
                <button className="btn btn-outline-warning" onClick={
                    ()=>{
                        setCreateView(false)
                    }
                }>
                    
                </button>
            </>
        
            :
            <>
                <button className="btn btn-outline-success mb-3"
                    onClick={()=>{
                        setCreateView(true)
                    }}>
                 
                </button>
                <div className="row justify-content-center">
                    <div className="table-responsive-md">
                        <table className="col-lg-8 col-md-10 col-sm-12 table table-striped p-3 shadow rounded">
                            <thead>
                                <tr>
                                    <th>Name</th>
                                    <th> Id</th>
                                    <th>Employee Commercials</th>
                                    <th>Employee Skills</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {tmpArray.map((ele,index)=>(
                                    <tr>
                                        <td>
                                            <button class="btn btn-outline-primary" onClick={()=>{
                                                setReadView(true)
                                                setPos(index)
                                            }}>
                                                <i class="bi bi-book-half"></i>
                                            </button>
                                            {ele.resName}
                                        </td>
                                        <td>{ele.resArea}</td>
                                        <td>{ele.resPay}</td>
                                        <td>{ele.resSkills}</td>
                                        <td>
                                            <button className="btn btn-outline-warning rounded-circle">
                                                Edit <i class="bi bi-pencil-fill"></i>
                                            </button>
                                        </td>
                                        <td>
                                            <button className="btn btn-outline-danger rounded-circle">
                                                Delete <i class="bi bi-trash-fill"></i>
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </>
            }
        </div>
    </>
)
}
    
